// Validate and process incoming emotion data
const data = $input.all()[0].json;

// Set defaults if missing
const processedData = {
  userId: data.userId || 'anonymous_' + Date.now(),
  emotion: data.emotion?.toLowerCase() || 'neutral',
  confidence: parseFloat(data.confidence) || 0,
  productId: data.productId || '',
  price: parseInt(data.price) || 0,
  category: data.category || 'Unknown',
  timestamp: data.timestamp || new Date().toISOString(),
  sessionId: data.sessionId || 'session_' + Date.now()
};

console.log('Processing emotion data:', processedData);

return { json: processedData };

// Count consecutive sad emotions for wellness mode trigger
const currentEmotion = $node["Process Emotion Data"].json.emotion;
const userId = $node["Process Emotion Data"].json.userId;
const recentEmotions = $input.all()[0].json.records || [];

// Count consecutive sad emotions
let consecutiveSad = 0;
for (let record of recentEmotions) {
  if (record.fields.Emotion === 'sad') {
    consecutiveSad++;
  } else {
    break; // Stop at first non-sad emotion
  }
}

// Add current emotion if it's sad
if (currentEmotion === 'sad') {
  consecutiveSad++;
}

// Determine if wellness mode should be triggered
const triggersWellnessMode = consecutiveSad >= 3;

console.log(`User ${userId} has ${consecutiveSad} consecutive sad emotions`);

return { 
  json: { 
    ...$node["Process Emotion Data"].json,
    consecutiveSad: consecutiveSad,
    triggersWellnessMode: triggersWellnessMode,
    recentEmotionCount: recentEmotions.length
  }
};

// Wellness Mode - Provide mental health support
const wellnessQuotes = [
  "Take time to breathe. You're doing better than you think. ðŸŒ¸",
  "Every purchase should bring you joy, not stress. ðŸ’",
  "Your wellbeing matters more than any product. ðŸ§˜â€â™€ï¸",
  "Sometimes the best buy is to not buy at all. ðŸŒ±",
  "Focus on what truly makes you happy. âœ¨",
  "You are enough, just as you are. ðŸ’š",
  "Take a moment to appreciate what you already have. ðŸ™"
];

const journalApps = [
  {
    name: "Daylio",
    url: "https://daylio.net",
    description: "Simple micro mood diary"
  },
  {
    name: "Journey",
    url: "https://journey.cloud",
    description: "Beautiful journaling experience"
  },
  {
    name: "Reflectly",
    url: "https://www.reflectly.app",
    description: "AI-powered journal"
  }
];

const calmingContent = [
  {
    type: "playlist",
    name: "Peaceful Piano",
    url: "https://open.spotify.com/playlist/37i9dQZF1DWZqd5JICZI0u",
    platform: "Spotify"
  },
  {
    type: "playlist",
    name: "Nature Sounds",
    url: "https://open.spotify.com/playlist/37i9dQZF1DX3Ogo9pFvBkY",
    platform: "Spotify"
  },
  {
    type: "meditation",
    name: "Headspace",
    url: "https://www.headspace.com/meditation/stress",
    platform: "Web"
  }
];

const motivationalGifs = [
  "https://media.giphy.com/media/3o7qDSOvfaCO9b3MlO/giphy.gif",
  "https://media.giphy.com/media/l2JhOVy5W9zqiOmB2/giphy.gif",
  "https://media.giphy.com/media/26FPJGjBiIWGoTNja/giphy.gif"
];

// Select random content
const randomQuote = wellnessQuotes[Math.floor(Math.random() * wellnessQuotes.length)];
const randomJournal = journalApps[Math.floor(Math.random() * journalApps.length)];
const randomContent = calmingContent[Math.floor(Math.random() * calmingContent.length)];
const randomGif = motivationalGifs[Math.floor(Math.random() * motivationalGifs.length)];

return {
  json: {
    type: "wellness",
    mode: "wellness_support",
    message: "We've noticed you might need some emotional support. Here's something to help you feel better:",
    consecutiveSadCount: $json.consecutiveSad,
    wellness: {
      quote: randomQuote,
      journalApp: randomJournal,
      calmingContent: randomContent,
      motivationalGif: randomGif,
      suggestion: "Consider taking a break from shopping and focus on self-care. Your mental health is more important than any purchase.",
      supportHotline: {
        name: "National Mental Health Helpline",
        number: "1-800-XXX-XXXX",
        available: "24/7"
      }
    },
    actionSuggestion: "pause_shopping"
  }
};

// Process cheaper alternatives for sad + expensive emotion
const alternatives = $input.all()[0].json.records || [];
const originalPrice = $node["Analyze Emotion Pattern"].json.price;
const category = $node["Analyze Emotion Pattern"].json.category;

if (alternatives.length === 0) {
  return {
    json: {
      type: "no_alternative",
      message: "We understand this might be outside your comfort zone. Here are some suggestions:",
      suggestions: [
        "Consider waiting for a sale or discount",
        "Look for similar products in our budget-friendly section",
        "Check if financing options are available",
        "Compare with other brands in the same category"
      ],
      category: category,
      originalPrice: originalPrice,
      actionSuggestion: "wait_for_sale"
    }
  };
}

// Process and format alternatives
const processedAlternatives = alternatives.map(record => ({
  id: record.id,
  name: record.fields.Name,
  price: record.fields.Price,
  originalPrice: originalPrice,
  savings: originalPrice - record.fields.Price,
  savingsPercentage: Math.round(((originalPrice - record.fields.Price) / originalPrice) * 100),
  rating: record.fields.Rating || 0,
  image: record.fields.Image?.[0]?.url || '',
  description: record.fields.Description || '',
  features: record.fields.Features ? record.fields.Features.split(',') : [],
  availability: record.fields.Availability || 'In Stock'
}));

// Get the best alternative (highest rating, most savings)
const bestAlternative = processedAlternatives[0];

return {
  json: {
    type: "cheaper_alternative",
    message: "We found some great alternatives that might be easier on your budget! ðŸ’°",
    emotion: "sad",
    context: "budget_conscious",
    bestAlternative: bestAlternative,
    allAlternatives: processedAlternatives,
    savingsHighlight: `Save â‚¹${bestAlternative.savings} (${bestAlternative.savingsPercentage}% off)`,
    actionSuggestion: "consider_alternative",
    supportMessage: "Remember, a good purchase should make you feel happy, not stressed about money."
  }
};

// Process premium/related products for happy emotion
const premiumProducts = $input.all()[0].json.records || [];
const currentPrice = $node["Analyze Emotion Pattern"].json.price;
const category = $node["Analyze Emotion Pattern"].json.category;
const confidence = $node["Analyze Emotion Pattern"].json.confidence;

// First, log this as a successful recommendation
const successLog = {
  userId: $node["Analyze Emotion Pattern"].json.userId,
  productId: $node["Analyze Emotion Pattern"].json.productId,
  emotion: "happy",
  confidence: confidence,
  timestamp: new Date().toISOString(),
  outcome: "positive_engagement"
};

if (premiumProducts.length === 0) {
  return {
    json: {
      type: "related_products",
      message: "Great choice! We're glad you're happy with this selection! ðŸ˜Š",
      emotion: "happy",
      context: "positive_reinforcement",
      successLog: successLog,
      suggestion: "You have excellent taste! Feel free to explore more in this category.",
      actionSuggestion: "explore_more"
    }
  };
}

// Process premium products
const processedProducts = premiumProducts.slice(0, 3).map(record => ({
  id: record.id,
  name: record.fields.Name,
  price: record.fields.Price,
  priceComparison: record.fields.Price > currentPrice ? 'premium' : 'similar',
  rating: record.fields.Rating || 0,
  image: record.fields.Image?.[0]?.url || '',
  description: record.fields.Description || '',
  features: record.fields.Features ? record.fields.Features.split(',') : [],
  premiumFeatures: record.fields.PremiumFeatures ? record.fields.PremiumFeatures.split(',') : [],
  availability: record.fields.Availability || 'In Stock',
  popularityScore: record.fields.PopularityScore || 0
}));

// Categorize recommendations
const premiumItems = processedProducts.filter(p => p.priceComparison === 'premium');
const similarItems = processedProducts.filter(p => p.priceComparison === 'similar');

return {
  json: {
    type: "premium_recommendation",
    message: "Since you're happy with this choice, you might also love these premium options! âœ¨",
    emotion: "happy",
    context: "upsell_opportunity",
    confidence: confidence,
    successLog: successLog,
    recommendations: {
      premium: premiumItems,
      similar: similarItems,
      all: processedProducts
    },
    personalizedMessage: confidence > 0.8 ? 
      "Your enthusiasm is contagious! Here are some premium picks just for you." :
      "We think you'll love these additional options in the same category.",
    actionSuggestion: "explore_premium"
  }
};

// Default response for neutral or other emotions
const emotion = $node["Analyze Emotion Pattern"].json.emotion;
const confidence = $node["Analyze Emotion Pattern"].json.confidence;
const category = $node["Analyze Emotion Pattern"].json.category;

// Emotion-specific responses
const emotionResponses = {
  neutral: {
    message: "Thanks for your interest! Here's what we recommend:",
    suggestion: "Take your time to consider your options. We're here to help!"
  },
  angry: {
    message: "We understand you might have concerns. Let us help you find the right solution.",
    suggestion: "Consider reading reviews or contacting our support team for more information."
  },
  fear: {
    message: "We want you to feel confident in your decision.",
    suggestion: "Check our return policy and customer reviews for peace of mind."
  },
  surprise: {
    message: "Surprised by this product? Let's help you understand it better!",
    suggestion: "Explore similar items or read detailed specifications."
  },
  disgust: {
    message: "This might not be the right fit. Let's find something better!",
    suggestion: "Browse alternative categories or filter by your preferences."
  }
};

const response = emotionResponses[emotion] || emotionResponses.neutral;

return {
  json: {
    type: "standard_response",
    emotion: emotion,
    confidence: confidence,
    message: response.message,
    suggestion: response.suggestion,
    category: category,
    supportOptions: {
      livechat: "Available 24/7",
      email: "support@yourstore.com",
      phone: "1-800-XXX-XXXX",
      faq: "https://yourstore.com/faq"
    },
    actionSuggestion: "browse_alternatives",
    additionalHelp: "Need assistance? Our customer service team is ready to help you find the perfect product."
  }
};

// Format final response for the Flask API
const response = $input.all()[0].json;
const originalData = $node["Analyze Emotion Pattern"].json;

// Create standardized response format
const finalResponse = {
  success: true,
  userId: originalData.userId,
  emotion: originalData.emotion,
  confidence: originalData.confidence,
  timestamp: new Date().toISOString(),
  processingTime: Date.now() - new Date(originalData.timestamp).getTime(),
  recommendation: response,
  metadata: {
    consecutiveSadCount: originalData.consecutiveSad || 0,
    wellnessModeTriggered: originalData.triggersWellnessMode || false,
    category: originalData.category,
    originalPrice: originalData.price,
    sessionId: originalData.sessionId
  },
  analytics: {
    emotionPattern: originalData.recentEmotionCount > 0 ? 'returning_user' : 'new_user',
    responseType: response.type,
    actionSuggestion: response.actionSuggestion || 'none'
  }
};

// Add wellness alert if needed
if (originalData.triggersWellnessMode) {
  finalResponse.wellnessAlert = {
    triggered: true,
    message: "Wellness mode activated due to multiple sad emotions",
    consecutiveCount: originalData.consecutiveSad
  };
}

console.log('Final response prepared:', JSON.stringify(finalResponse, null, 2));

return { json: finalResponse };
